package AConciseIntroductionToDataStructuresUsingJava.List;// For LinkedList class, Listing 6.2import java.util.NoSuchElementException;// For LinkedList class, Listing 6.3public class LinkedListIterator<E> implements Iterator<E> {    private Node<E> head = null;    private Node<E> next = head.next;    private Node<E> prev = new Node<>(null, next, head);    private int size = 0;// skip dummy    private class Node<T> {        private T data;        private Node<T> next;        private Node<T> prev;        public Node(T data, Node<T> next, Node<T> prev) {            this.data = data;            this.next = next;            this.prev = prev;        }    }    public boolean hasNext() {        return next != null;    }    public E next() {        if (!hasNext()) throw new NoSuchElementException();        E result = next.data;        next = next.next;        return result;    }    public void remove() {        throw new UnsupportedOperationException();    }    public E removeAt(int index) {        if (index < 0 || index >= size)            throw new IndexOutOfBoundsException();        Node<E> p = getNode(index);        E result = p.data;        p.prev.next = next;        if (next != null) p.next.prev = prev;        size--;        return result;    }    private Node<E> getNode(int index) {        // assumes -1 <= index < size.  -1 is dummy.        Node<E> p = head;        for (int i = -1; i < index; i++) {            p = p.next;        }        return p;    }    public Iterator<E> iterator() {        return new LinkedListIterator();    }    public static void main(String[] args){        //for(Node<T> p = head; p != null; p = p.next)    }}