package AConciseIntroductionToDataStructuresUsingJava.Queue;// Listing 5.2public class FixedLengthQueue<E> extends ArrayQueue1<E> {   private int drops;      public FixedLengthQueue(int capacity) {      super(capacity);   }      @Override   public void enqueue(E item) { // complexity-time constant - O(1)      if (size == capacity) {         drops++;      } else {         super.enqueue(item);      }   }      public int drops() {      return drops;   }   public static void main(String[] args){       Queue1<Integer> in = new FixedLengthQueue<>(10);       Queue1<Double> d = new FixedLengthQueue<>(10);       Queue1<String> s = new FixedLengthQueue<>(10);       in.enqueue(5);       in.enqueue(8);       System.out.println(in.peek());       in.enqueue(3);       System.out.println(in.dequeue());       in.enqueue(10);       System.out.println(in.size());       in.enqueue(3);       System.out.println(in.dequeue());       System.out.println(in.dequeue());       d.enqueue(1.0);       d.enqueue(2.0);       d.enqueue(3.0);       d.enqueue(4.0);       System.out.println(d.peek());       d.enqueue(5.0);       System.out.println(d.dequeue());       System.out.println(d.size());       System.out.println(d.dequeue());       System.out.println(d.dequeue());       s.enqueue("Java");       System.out.println(s.dequeue());       s.enqueue("SQL");       System.out.println(s.dequeue());       s.enqueue("Oracle");       System.out.println(s.dequeue());       System.out.println(s.size());       s.enqueue("Javascript");       System.out.println(s.peek());       System.out.println(s.dequeue());       FixedLengthQueue<Integer> q = new FixedLengthQueue<>(50); //Type Queue1 doesn't compile; Queue1 is not       for (int i = 0; i < 100; i++) {                                   //the superclass of FixedLengthQueue           q.enqueue(i);       }       System.out.println(q.drops());   }}