package AConciseIntroductionToDataStructuresUsingJava.Tree;import AConciseIntroductionToDataStructuresUsingJava.Queue.LinkedQueue1;import AConciseIntroductionToDataStructuresUsingJava.Queue.Queue1;// Listing 8.2public abstract class BinaryTree<E> {   protected Node<E> root;      public String preorder() {      return preorder(root);   }      private String preorder(Node<E> n) {      if (n == null) return "";      return (n + " " + preorder(n.left) + " " +            preorder(n.right)).trim().replaceAll("\\s+", " ");   }   public String breadthFirst(){       return breadthFirst(root);   }   private String breadthFirst(Node<E> n) {       if(n==null) return "";       Queue1<Node<E>> queue = new LinkedQueue1<>();       StringBuilder builder = new StringBuilder();       queue.enqueue(n);       while(n.left!=null || n.right!=null || !queue.isEmpty()){           if(n.left!=null) queue.enqueue(n.left);           if(n.right!=null) queue.enqueue(n.right);           builder.append(queue.dequeue()).append(" ");           if(!queue.isEmpty()) n = queue.peek();       }       return builder.toString();   }    public String inorder(){       return inorder(root);    }    protected String inorder(Node<E> n) {       if(n==null) return "";       return (inorder(n.left) + " " + n + " " +                inorder(n.right)).trim().replaceAll("\\s+", " ");    }    public String postorder(){       return postorder(root);    }    private String postorder(Node<E> n) {       if(n==null) return "";       return (postorder(n.left) + " " +                postorder(n.right)+ " " + n ).trim().replaceAll("\\s+", " ");    }    @Override    public String toString() {        return preorder(root);    }    protected static class Node<T> {      protected T data;      protected Node<T> left, right, parent;            protected Node(T data, Node<T> parent) {         this.data = data;         this.parent = parent;      }            public String toString() {         return data.toString();      }   }   public static void main(String[] args){       Node<Integer> root = new Node<>(9,null);       Node<Integer> firstChild = new Node<>(10, root);       root.left = firstChild;       Node<Integer> secondChild = new Node<>(18, root);       root.right = secondChild;       firstChild.right = new Node<>(12, firstChild);       secondChild.left = new Node<>(16, secondChild);//       BinaryTree<Integer> tree = new BinaryTree<Integer>();//       tree.root = root;//       System.out.println(tree.breadthFirst());//       System.out.println(tree.preorder());//       System.out.println(tree.inorder());//       System.out.println(tree.postorder());//       System.out.println(tree.toString());   }}